import React, { Component } from 'react';
import MiComponente from './MiComponente';
import Peliculas from './Peliculas';


class SeccionPruebas extends Component {

    //Podemos tener varias propiedades, ejemplo:
    contador = 0; //Y después mostrarla abajo
    
    constructor(props){
        
    }



    HolaMundo(name, edad) { //Como esta dentro de la clase puedo definirla de esta manera, sin poner el function
        var introduction =
            <div>
                <h2>Hola, soy {name} </h2>
                <h3>Tengo {edad} años </h3>
            </div>;

        return introduction;
    }

    sumar(){
        this.contador = this.contador+1;

    }
    restar(){
        //Seria lo mismo poner this.contador--;
        this.contador = this.contador-1;
    }

    render() {

        var name = "Roberto Teixeira";

        return (
            <section id="content">

                <h2 class="subheader">Últimos artículos</h2>
                <p>
                    Hola bienvenido al curso de React.JS de Victor Robles.
                </p>

                <h2 class="subheader">Funciones y JSX Básico</h2>
                {this.HolaMundo(name, 24)}

                <h2 class="subheader">Componentes</h2>
                <section className="componentes">
                    <MiComponente />
                    <Peliculas />
                </section>

                <h2 class="subheader">El Estado</h2>
                <p>
                    Contador: {this.contador} 
                </p> 
                <p>
                    <input type="button" value="Sumar" onClick={this.sumar.bind(this)}/>
                    <input type="button" value="Restar" onClick={this.restar.bind(this)}/>
                </p>
                

            </section>
        );
    }
}

export default SeccionPruebas;
