import logo from './logo.svg';
import './App.css';


function HolaMundo(name, edad) {
  var introduction =
    <div>
      <h2>Hola, soy {name} </h2>
      <h3>Tengo {edad} años </h3>
    </div>;

  return introduction;
}

function App() {

  var name = "Roberto Teixeira";

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>

        {HolaMundo(name, 24)}

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;

//Un componenete es un trozo de pantalla, es decir, la  parte lógica que controla un trozo de pantalla. Lo que vemos en la página son varios trozos de pantallas. 
//Lo que hace un componente es ser un mediador entre lo que ve el usuario y la lógica de la aplicación, os sea, un componente es una especie de controlador que recibe datos, procesa esos datos y pide datos a los modelos ejecutando una lógica para sacar información de un backend
